using System;
using System.Collections.Generic;
using System.Text.Json.Serialization;
using Newtonsoft.Json;

namespace RepairPlanner.Models;

/// <summary>
/// Represents a repair work order generated by the Repair Planner Agent.
/// </summary>
public sealed class WorkOrder
{
    [JsonPropertyName("id")]
    [JsonProperty("id")]
    public string Id { get; set; } = string.Empty;

    [JsonPropertyName("workOrderNumber")]
    [JsonProperty("workOrderNumber")]
    public string WorkOrderNumber { get; set; } = string.Empty;

    [JsonPropertyName("machineId")]
    [JsonProperty("machineId")]
    public string MachineId { get; set; } = string.Empty;

    [JsonPropertyName("title")]
    [JsonProperty("title")]
    public string Title { get; set; } = string.Empty;

    [JsonPropertyName("description")]
    [JsonProperty("description")]
    public string? Description { get; set; }

    [JsonPropertyName("type")]
    [JsonProperty("type")]
    public string? Type { get; set; }

    [JsonPropertyName("priority")]
    [JsonProperty("priority")]
    public string? Priority { get; set; }

    /// <summary>
    /// Cosmos partition key in sample dataset.
    /// </summary>
    [JsonPropertyName("status")]
    [JsonProperty("status")]
    public string Status { get; set; } = "new";

    [JsonPropertyName("assignedTo")]
    [JsonProperty("assignedTo")]
    public string? AssignedTo { get; set; }

    [JsonPropertyName("createdDate")]
    [JsonProperty("createdDate")]
    public DateTimeOffset CreatedDate { get; set; } = DateTimeOffset.UtcNow;

    [JsonPropertyName("scheduledDate")]
    [JsonProperty("scheduledDate")]
    public DateTimeOffset? ScheduledDate { get; set; }

    [JsonPropertyName("completedDate")]
    [JsonProperty("completedDate")]
    public DateTimeOffset? CompletedDate { get; set; }

    [JsonPropertyName("estimatedDuration")]
    [JsonProperty("estimatedDuration")]
    public int? EstimatedDurationMinutes { get; set; }

    [JsonPropertyName("actualDuration")]
    [JsonProperty("actualDuration")]
    public int? ActualDurationMinutes { get; set; }

    [JsonPropertyName("downtime")]
    [JsonProperty("downtime")]
    public int? DowntimeMinutes { get; set; }

    [JsonPropertyName("cost")]
    [JsonProperty("cost")]
    public decimal? Cost { get; set; }

    [JsonPropertyName("partsUsed")]
    [JsonProperty("partsUsed")]
    public List<WorkOrderPartUsage> PartsUsed { get; set; } = new();

    [JsonPropertyName("notes")]
    [JsonProperty("notes")]
    public string? Notes { get; set; }

    [JsonPropertyName("tasks")]
    [JsonProperty("tasks")]
    public List<RepairTask> Tasks { get; set; } = new();

    [JsonPropertyName("sourceFault")]
    [JsonProperty("sourceFault")]
    public DiagnosedFault? SourceFault { get; set; }
}
